# Runtime
# 329 ms
# Beats
# 65.43%
# Memory
# 20.4 MB
# Beats
# 87.31%
class Solution:
    def countPaths(self, n: int, roads: List[List[int]]) -> int:
        MOD = 10 ** 9 + 7
        edges = defaultdict(list)
        for u, v, time in roads:
            edges[u].append((v, time))
            edges[v].append((u, time))
        BigValue = float('Inf')
        dp = [[0, BigValue] for _ in range(n)]
        to_visit = [(0, 0)]
        dp[0][0] = 1
        dp[0][1] = 0
        while to_visit:
            t, current = heapq.heappop(to_visit)
            if dp[current][1] < t:
                continue
            for node, time in edges[current]:
                new_time = t + time
                if dp[node][1] > new_time:
                    dp[node][0] = dp[current][0]
                    dp[node][1] = new_time
                    heapq.heappush(to_visit, (new_time, node))
                elif dp[node][1] == new_time:
                    dp[node][0] += dp[current][0]
                    dp[node][0] %= MOD

        return dp[n-1][0] % MOD


