class Solution:
    def findSubstringInWraproundString(self, p: str) -> int:
        n = len(p)
        starts = [0] * 26
        dp = 1
        for i in range(n-1, -1, -1):
            if i < n-1 and ord(p[i+1])-ord('a') == (ord(p[i])-ord('a') + 1) % 26:
                dp += 1
            else:
                dp = 1
            starts[ord(p[i]) - ord('a')] = max(starts[ord(p[i]) - ord('a')], dp)
        return sum(starts)


tests = [
    ["", 259259],
    ["", 13871],
    ["zaba", 6], # zab (z za zab) ab (a ab) b
    ["a", 1],
    ["cac", 2],
    ["zab", 6]
]

run_functional_tests(Solution().findSubstringInWraproundString, tests)
